mapper.select:
SELECT * FROM account
 WHERE email = #{email}
   AND pwd = #{pwd}

mapper.insert:
INSERT INTO account(id, nickname, name, pwd, email, good, bad)
     VALUES(account_seq.NEXTVAL, #{nickname}, #{name}, #{pwd}
            , #{email}, #{good}, #{bad})

mapper.update:
UPDATE account
   SET nickname = #{nickname}
 WHERE id = #{id}
